}

export function IndicatorCard({ 
  indicator, 
  onAddWitness, 
  onViewDetails, 
  onDelete,
  onToggleCriteria 
}: IndicatorCardProps) {
  const getStatusBadge = () => {
    switch (indicator.status) {
      case "completed":
        return (
          <Badge className="bg-success text-success-foreground" data-testid={`badge-status-${indicator.id}`}>
            Ù…ÙƒØªÙ…Ù„
