        );
      case "in_progress":
        return (
          <Badge className="bg-primary text-primary-foreground" data-testid={`badge-status-${indicator.id}`}>
            Ù‚ÙŠØ¯ Ø§Ù„ØªÙ†ÙÙŠØ°
