import type { Config } from "tailwindcss";

export default {
  darkMode: ["class"],
  content: ["./client/index.html", "./client/src/**/*.{js,jsx,ts,tsx}"],
  theme: {
    extend: {
      borderRadius: {
        lg: ".625rem", /* 10px */
        md: ".5rem", /* 8px */
        sm: ".375rem", /* 6px */
